package yl.demo.pathHelper.pathSearching.location;

public class Location {
	public Location(int buildingId, int floorId, double x, double y) {
		// TODO Auto-generated constructor stub
		this.buildingId = buildingId;
		this.floorId = floorId;
		this.x = x;
		this.y = y;
	}
	public int buildingId;
	public int floorId;
	public double x;
	public double y;
}
